    private void btnGuardarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnGuardarActionPerformed
        if (this.CNombre.getText().equals("")) {
            JOptionPane.showMessageDialog(this, "El Nombre es Requerido.", "Aviso", JOptionPane.WARNING_MESSAGE);
            this.CNombre.requestFocus();
            return;
        }

        if (this.CApellido.getText().equals("")) {
            JOptionPane.showMessageDialog(this, "La Descripcion es Requerida.", "Aviso", JOptionPane.WARNING_MESSAGE);
            this.CApellido.requestFocus();
            return;
        }

        if (this.CDireccion.getText().equals("")) {
            JOptionPane.showMessageDialog(this, "El Precio de Venta es Requerido.", "Aviso", JOptionPane.WARNING_MESSAGE);
            this.CDireccion.requestFocus();
            return;
        }

        if (this.CTelefono.getText().equals("")) {
            JOptionPane.showMessageDialog(this, "El Stock es Requerido.", "Aviso", JOptionPane.WARNING_MESSAGE);
            this.CTelefono.requestFocus();
            return;
        }

        int operacion = new LCliente().Guardar(new Cliente(
            0,
            this.CNombre.getText().trim(),
            this.CApellido.getText().trim(),
            this.CDireccion.getText().trim(),
            Integer.parseInt(this.CTelefono.getText().trim()),
            this.CCorreo.getText().trim()
        ));

        if (operacion > 0) {
            JOptionPane.showMessageDialog(this, "Se complet√≥ el registro correctamente.", "Aviso", JOptionPane.INFORMATION_MESSAGE);
            this.LimpiarFormulario();
        } else {
            JOptionPane.showMessageDialog(this, "No se pudo completar el registro del producto,"
                + " intente nuevamente, verifique que no exista el producto ingresado.", "Aviso", JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_btnGuardarActionPerformed

    private void LimpiarFormulario() {
        this.CNombre.setText("");
        this.CApellido.setText("");
        this.CDireccion.setText("");
        this.CTelefono.setText("");
        this.CCorreo.setText("");
    }
    
