    private void btnGuardarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnGuardarActionPerformed
        if (this.UNombre.getText().equals("")) {
            JOptionPane.showMessageDialog(this, "El Nombre es Requerido.", "Aviso", JOptionPane.WARNING_MESSAGE);
            this.UNombre.requestFocus();
            return;
        }

        if (this.UApellido.getText().equals("")) {
            JOptionPane.showMessageDialog(this, "La Apellido es Requerido.", "Aviso", JOptionPane.WARNING_MESSAGE);
            this.UApellido.requestFocus();
            return;
        }

        if (this.UPuesto.getText().equals("")) {
            JOptionPane.showMessageDialog(this, "El Puesto es Requerido.", "Aviso", JOptionPane.WARNING_MESSAGE);
            this.UPuesto.requestFocus();
            return;
        }

        if (this.UTelefono.getText().equals("")) {
            JOptionPane.showMessageDialog(this, "El Telefono es Requerido.", "Aviso", JOptionPane.WARNING_MESSAGE);
            this.UTelefono.requestFocus();
            return;
        }
        
        if (this.UCorreo.getText().equals("")) {
            JOptionPane.showMessageDialog(this, "El Correo es Requerido.", "Aviso", JOptionPane.WARNING_MESSAGE);
            this.UCorreo.requestFocus();
            return;
        }

        int operacion = new LUsuario().Guardar(new Usuario(
            0,
            this.UNombre.getText().trim(),
            this.UApellido.getText().trim(),
            this.UPuesto.getText().trim(),
            Integer.parseInt(this.UTelefono.getText().trim()),
            this.UCorreo.getText().trim()
        ));

        if (operacion > 0) {
            JOptionPane.showMessageDialog(this, "Se completó el registro correctamente.", "Aviso", JOptionPane.INFORMATION_MESSAGE);
            this.LimpiarFormulario();
        } else {
            JOptionPane.showMessageDialog(this, "No se pudo completar el registro del producto,"
                + " intente nuevamente, verifique que no exista el producto ingresado.", "Aviso", JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_btnGuardarActionPerformed

